import React, { useEffect, useState } from "react";

function Buyers(params) {
    const[buyers,setbuyers]=useState([])
    const[newbuyer,setnewbuyer]=useState({title:"",category:"",price:""})

    function onsubmit(e) {
        e.preventDefault();

    }
    function onchange(e) {
        setnewbuyer({...newbuyer,[e.target.name]:e.target.value})
    }
    useEffect(()=>{
const configObject={
    method:"POST",
    headers:{"Content-Type":"application/json"},
    body:JSON.stringify(
        {
            title:newbuyer.title,
            category:newbuyer.category,
            price:newbuyer.price 
        }
    )
}
fetch("https://fakestoreapi.com/products",configObject)
.then (res=>res.json())
.then (data=>setbuyers(...buyers,data))

    },[])
    useEffect(()=>{
        fetch("https://fakestoreapi.com/products")
        .then((res)=>res.json())
        .then(data=>setbuyers(data))

    },[])
    return(
        <div>
            <form style={{width:"70%",margin:"20px"}}   onChange={onchange}  onSubmit={onsubmit} >
                <h2>Add a new buyer</h2>
                    <div className="mb-3">
                        <label for="titleinput" className="form-label">Title</label>
                        <input type="text" className="form-control" id="titleinput" name="title" value={newbuyer.title}/>
                        <div id="emailHelp" className="form-text">please input your title.</div>
                    </div>
                    <div className="mb-3">
                        <label for="categoryinput" className="form-label">Category</label>
                        <input type="text" className="form-control" id="categoryinput" name="category" value={newbuyer.category}/>
                        <div id="emailHelp" className="form-text">please input your category.</div>
                    </div>
                    <div className="mb-3">
                        <label for="priceinput" className="form-label">Price</label>
                        <input type="text" className="form-control" id="priceinput"/>
                        <div id="emailHelp" className="form-text">please input your price.</div>
                    </div>
                    <button type="submit" className="btn btn-primary">Add Item</button>
            </form>
            <table className="table table-bordered">
                    <thead>
                        <tr>
                        <th scope="col">#</th>
                        <th scope="col">TITLE</th>
                        <th scope="col">PRICE</th>
                        <th scope="col">CATEGORY</th>
                        </tr>
                    </thead>
            {buyers.map((buyers)=>

                    <tbody>
                        <tr>
                        <th scope="row">{buyers.id}</th>
                        <td>{buyers.title}</td>
                        <td>{buyers.price}</td>
                        <td>{buyers.category}</td>
                        </tr>
                    </tbody>

)}
 </table>
        </div>
    );
}
export default Buyers;
