import React, { useEffect, useState } from "react";

function Buyers(params) {
    const[buyers,setbuyers]=useState([])
    const[newbuyer,setnewbuyer]=useState({title:"",category:"",price:""})

    function onsubmit(e) {
        e.preventDefault();
        useEffect(()=>{
            const configObject={
                method:"POST",
                headers:{"Content-Type":"application/json"},
                body:JSON.stringify(newbuyer)
            }
            fetch("https://fakestoreapi.com/products",configObject)
            .then (res=>res.json())
            .then (data=>setbuyers(currentdata=>[...currentdata,data]))

                },[])

    }
    function onchange(e) {
        setnewbuyer({...newbuyer,[e.target.name]:e.target.value})
    }

    useEffect(()=>{
        fetch("https://fakestoreapi.com/products")
        .then((res)=>res.json())
        .then(data=>setbuyers(data))

    },[])
    return(
        <div>
            ,
            <table className="table table-bordered">
                    <thead>
                        <tr>
                        <th scope="col">#</th>
                        <th scope="col">TITLE</th>
                        <th scope="col">PRICE</th>
                        <th scope="col">CATEGORY</th>
                        </tr>
                    </thead>
            {buyers.map((buyers)=>

                    <tbody>
                        <tr>
                        <th scope="row">{buyers.id}</th>
                        <td>{buyers.title}</td>
                        <td>{buyers.price}</td>
                        <td>{buyers.category}</td>
                        </tr>
                    </tbody>

)}
 </table>
        </div>
    );
}
export default Buyers;
